'use client';

import * as React from 'react';
import { Progress } from '@/registry/default/ui/base-progress';

export default function BaseProgressAnimated() {
  const [progress, setProgress] = React.useState(0);

  React.useEffect(() => {
    const timer = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          return 0; // Reset for continuous loop
        }
        return prev + Math.random() * 2 + 0.5; // Random increment 0.5-2.5
      });
    }, 200);

    return () => {
      clearInterval(timer);
    };
  }, []);

  return (
    <div className="w-full max-w-xs space-y-2">
      <h3 className="text-sm font-medium">Pulsing Animation</h3>
      <Progress
        value={progress}
        className="[&_[data-slot=progress-indicator]]:bg-purple-600 [&_[data-slot=progress-indicator]]:animate-pulse"
      ></Progress>
      <div className="inline-flex items-center gap-2 text-xs text-muted-foreground">
        Processing data...
        <span>{Math.round(progress)}%</span>
      </div>
    </div>
  );
}

import * as React from 'react';
import { cn } from '@/registry/default/lib/utils';
import { Progress as BaseProgress } from '@base-ui-components/react/progress';

function Progress({ className, children, ...props }: React.ComponentProps<typeof BaseProgress.Root>) {
  return (
    <BaseProgress.Root data-slot="progress" className="relative" {...props}>
      <BaseProgress.Track
        data-slot="progress-track"
        className={cn('bg-secondary block h-1.5 w-full overflow-hidden rounded-full', className)}
      >
        <BaseProgress.Indicator
          data-slot="progress-indicator"
          className="bg-primary block h-full w-full transition-all"
        />
      </BaseProgress.Track>
      {children}
    </BaseProgress.Root>
  );
}

function ProgressValue({ className, ...props }: React.ComponentProps<typeof BaseProgress.Value>) {
  return (
    <BaseProgress.Value
      data-slot="progress-value"
      className={cn('text-foreground mt-1.5 flex justify-end text-sm font-medium', className)}
      {...props}
    />
  );
}

export { Progress, ProgressValue };

import { clsx, type ClassValue } from 'clsx';
import { twMerge } from 'tailwind-merge';

/**
 * Merges Tailwind class names, resolving any conflicts.
 *
 * @param inputs - An array of class names to merge.
 * @returns A string of merged and optimized class names.
 */
export function cn(...inputs: ClassValue[]): string {
  return twMerge(clsx(inputs));
}
