            {/* Custom gradient track background with glow effect */}
            <div
              className="absolute inset-0 h-3 rounded-full opacity-20 shadow-lg"
              style={{
                background: 'linear-gradient(to right, #ec4899, #a855f7, #3b82f6, #14b8a6, #eab308, #ef4444)',
              }}
